mapper.select:
SELECT count(*) as cnt FROM ( SELECT c.cmntor_no, name, birth_dt, gender, eng_name, arrg_place, (select act_prt_nm from act_prt ap where ap.act_prt_cd = c.act_prt_cd) as act_prt_nm, sido_cd, (select sido_cd_nm from sido s where c.sido_cd = s.sido_cd) as sido_nm, gugun_cd, (select gugun_cd_nm from gugun g where c.gugun_cd = g.gugun_cd) as gugun_nm FROM cmntor c WHERE ifnull(del_yn, 'N') <> 'Y'
AND c.sido_cd = #{srch_sido}
AND c.gugun_cd = case when #{srch_gugun} like '%000' then c.gugun_cd else #{srch_gugun} end
AND c.name like concat('%', #{srch_nm}, '%')
AND c.cmntor_no = #{cmntor_no}
AND c.act_yn = #{srch_act_yn}
AND c.auth_no = #{auth_no}
) tt WHERE 1 =1
AND (sido_nm like concat('%', #{srch_area}, '%') or gugun_nm like concat('%', #{srch_area}, '%') )

select bs.* from ( SELECT @rownum:=@rownum+1 as row_num, tt.* FROM ( SELECT c.cmntor_no, name, birth_dt, case when substr(birth_dt,1,2) in ('19','20') then substr(now(),1,4) - substr(birth_dt,1,4) + 1 else '' end age, gender, eng_name, eng_name2, arrg_place, c.rgst_dt, act_yn, (select act_prt_nm from act_prt ap where ap.act_prt_cd = c.act_prt_cd) as act_prt_nm, sido_cd, (select sido_cd_nm from sido s where c.sido_cd = s.sido_cd) as sido_cd_nm, gugun_cd, hand_phone, (select gugun_cd_nm from gugun g where c.gugun_cd = g.gugun_cd) as gugun_cd_nm, SUBSTRING_INDEX(hand_phone,'-',-1) as hand_ph3, group_concat( case when l.lang_cd = '09' then l.etc_lang else la.lang_nm end order by la.lang_cd separator '/' ) as lang_nm FROM cmntor c left join cmntor_lang l on c.cmntor_no = l.cmntor_no left join lang la on l.lang_cd = la.lang_cd WHERE ifnull(del_yn, 'N') <> 'Y'
AND c.sido_cd = #{srch_sido}
AND c.gugun_cd = case when #{srch_gugun} like '%000' then c.gugun_cd else #{srch_gugun} end
AND c.name like concat('%', #{srch_nm}, '%')
AND c.cmntor_no = #{cmntor_no}
AND c.act_yn = #{srch_act_yn}
AND c.auth_no = #{auth_no}
group by c.cmntor_no, name, birth_dt, gender, hand_phone, eng_name, eng_name2, act_prt_cd, act_yn, arrg_place, c.rgst_dt, sido_cd, gugun_cd ) tt , (select @rownum:=0) tmp WHERE 1 = 1
AND (sido_cd_nm like concat('%', #{srch_area}, '%') or gugun_cd_nm like concat('%', #{srch_area}, '%') )
ORDER BY
name
name desc
rgst_dt, cmntor_no
) bs order by row_num desc limit #{startRow}, #{endRow}

mapper.insert:
INSERT INTO cmntor(cmntor_no, name, birth_dt, gender, eng_name, eng_name2, rgst_dt, act_yn,  act_prt_cd, sido_cd, gugun_cd,
				last_edu_cd, home_zip, home_addr1, home_addr2, home_phone, hand_phone, email, arrg_place, hope_place,
				prfl_img, job, etc_job_nm, eng_test_yy, eng_test_nm, eng_test_pnt, chn_test_yy, chn_test_nm, chn_test_pnt, 
				jpn_test_yy, jpn_test_nm, jpn_test_pnt,	etc_lang_nm, etc_test_yy, etc_test_nm, etc_test_pnt,
				auth_no, inpt_usr, inpt_tm, updt_usr, updt_tm)
SELECT
	
	
	     
			CONCAT(#{gugun_cd}, LPAD(IFNULL((SELECT max(convert(substring(cmntor_no,4), decimal))+1 FROM cmntor WHERE cmntor_no like concat(#{gugun_cd},'%') ),1), 4, '0'))
		
		
			CONCAT(#{sido_cd}, LPAD(IFNULL((SELECT max(convert(substring(cmntor_no,2), decimal))+1 FROM cmntor WHERE cmntor_no like concat(#{sido_cd},'%') ),1), 4, '0'))
		
	
	,#{name}
	,#{birth_dt}
	,#{gender}
	,#{eng_name}
	,#{eng_name2}
	,#{rgst_dt}
	,#{act_yn}
	,#{act_prt_cd}
	,#{sido_cd}
	,ifnull(#{gugun_cd},'')
	,#{last_edu_cd}
	,#{home_zip}
	,#{home_addr1}
	,#{home_addr2}
	,#{home_phone}
	,#{hand_phone}
	,#{email}
	,#{arrg_place}
	,#{hope_place}
	,#{prfl_img}
	,#{job}
	,#{etc_job_nm}
	,#{eng_test_yy}
	,#{eng_test_nm}
	,#{eng_test_pnt}
	,#{chn_test_yy}
	,#{chn_test_nm}
	,#{chn_test_pnt}
	,#{jpn_test_yy}
	,#{jpn_test_nm}
	,#{jpn_test_pnt}
	,#{etc_lang_nm}
	,#{etc_test_yy}
	,#{etc_test_nm}
	,#{etc_test_pnt}
	,#{auth_no}
	,#{inpt_usr}
	,now()
	,#{inpt_usr}
	,now()

	select max(cmntor_no) as cmntor_no 
	FROM cmntor 
	WHERE 1 = 1 
	
	     
		AND	cmntor_no like concat(#{gugun_cd},'%')
		
		
		AND	cmntor_no like concat(#{sido_cd},'%')

UPDATE cmntor
SET 
