WHERE
	cmntor_no = #{cmntor_no}

mapper.update:
UPDATE edu_cmpl_hist
SET
	edu_prt = #{edu_prt},
    yyyy = #{yyyy},
    edu_place = #{edu_place},
    edu_nm = #{edu_nm},
    edu_tm = #{edu_tm},
    edu_cmpl = #{edu_cmpl}
WHERE cmntor_no = #{cmntor_no}
AND edu_seq = #{edu_seq}

mapper.insert:
DELETE FROM act_hist
WHERE
	cmntor_no = #{0} 
	AND seq = #{1}

DELETE FROM act_fee_pay_hist
WHERE
	cmntor_no = #{0} 
	AND seq = #{1}

