		,birth_dt = #{birth_dt}
	
	
		,home_addr1 = #{home_addr1}
	
	
		,home_addr2 = #{home_addr2}
	 
	
		,home_phone = #{home_phone}
	
	
		,hand_phone = #{hand_phone}
	
		
		,email = #{email}
	
WHERE
	cmntor_no = #{cmntor_no}

mapper.select:
	SELECT 
		count(*) as cnt
	FROM (
		select @rownum:=@rownum+1 as row_num, c.* 
        from  cmntor c join (select @rownum:=0) tmp                     
		WHERE ifnull(del_yn, 'N') != 'Y' 
		AND	c.sido_cd = #{srch_sido}
		
			AND	c.gugun_cd = #{srch_gugun} 
		
		) c
		left join act_tot_hist a on c.cmntor_no = a.cmntor_no and a.work_ym = #{srch_ym}
	WHERE ifnull(del_yn, 'N') <> 'Y'

select bs.* from ( SELECT c.row_num, c.name, c.cmntor_no, c.sido_cd, c.gugun_cd, (select gugun_yn from sido s where c.sido_cd = s.sido_cd) as srch_area, c.eng_name, c.eng_name2, a.work_ym, a.work_dt_cnt, a.work_tm, a.work_place, a.visitor_cnt, a.cmmt_cnt, a.act_fee, a.pay_yn FROM ( select @rownum:=@rownum+1 as row_num, c.* from cmntor c join (select @rownum:=0) tmp WHERE ifnull(del_yn, 'N') != 'Y' AND c.sido_cd = #{srch_sido}
AND c.gugun_cd = #{srch_gugun}
) c left join act_tot_hist a on c.cmntor_no = a.cmntor_no and a.work_ym = #{srch_ym} WHERE ifnull(del_yn, 'N') <> 'Y' ) bs order by row_num desc limit #{startRow}, #{endRow}
